export const cfetch=()=>{contentful.createClient({space:process.env.SpaceID,accessToken:process.env.ContentDeliveryApi}).sync({initial:!0}).then(e=>{e.entries.forEach(e=>{const n=e.fields;if(n.hasOwnProperty("background"));else if(n.hasOwnProperty("videoLink")){const e=`\n            <img src=${n.image["en-US"].fields.file["en-US"].url} alt="">\n           <p class="title" >${n.title["en-US"]}  </p>\n           \n           <p id="description" style="display:none">\n             ${n.description["en-US"]}\n           </p>\n           <video src="${n.videoLink["en-US"]}"  controls style="width: 100%; height: auto; display:none"></video>\n          `,i=`<img src=${n.image["en-US"].fields.file["en-US"].url} alt=""> `,t=document.createElement("div"),s=document.createElement("div");s.className="previews",s.innerHTML=i,t.className="movie",t.innerHTML=e,document.querySelector(".movieContainer").append(t),document.querySelector(".slide").append(s)}else{const e=`\n             <img src=${n.image["en-US"].fields.file["en-US"].url} alt="">\n            <p class="title" >${n.title["en-US"]}  </p>\n            \n            <p id="description" style="display:none">\n              ${n.description["en-US"]}\n            </p>\n            <video src=""  controls style="width: 100%; height: auto; display:none"></video>\n            `,i=`<img src=${n.image["en-US"].fields.file["en-US"].url} alt=""> `,t=document.createElement("div"),s=document.createElement("div");s.className="previews",s.innerHTML=i,t.className="movie",t.innerHTML=e,document.querySelector(".movieContainer").append(t),document.querySelector(".slide").append(s)}})})};