export const description=e=>{let t,r;if("far fa-window-close"==e.target.className){document.querySelector(".movieDesDisplay").style.display="none";e.target.parentElement.querySelector("video").pause()}else"movie"==e.target.parentElement.className&&(t=e.target.parentElement.querySelector("#description").textContent,r=e.target.parentElement.querySelector(".title").textContent,""!==e.target.parentElement.querySelector("video").src&&(document.querySelector(".video").src=e.target.parentElement.querySelector("video").src),document.querySelector(".movieDesDisplay #l").textContent=t,document.querySelector(".movieDesDisplay #title").textContent=r,document.querySelector(".movieDesDisplay").style.display="flex")};